for (var i = 0, l = 64; i < l; i++) {
  var b = v.blocks[i];
  b.position.x = 100 +  (i % 8)  * 48;
  b.position.y = 100 + mod(i, 8) * 6;
  b.scale(0.95);
  b.rotate(Math.random()*100);
}

