for (var i = 0, l = 144; i < l; i++) {
  var b = new Path.Circle( [ 300, 300 ], 10 );
  var x_off = Math.sin(i*1) * 4,
      y_off = Math.cos(i*1) * 4;
  b.position.x = 38 +  (i % 12) * 48 + x_off;
  b.position.y = 48 + mod(i, 12) * 4 + y_off;

  b.fillColor = '#0000ff';

  //if (i % 999) {
    b.fillColor.saturation = 0;
    b.fillColor.brightness = 0.999;
  //}
  b.segments[0].point.x += 5;
  b.segments[2].point.x -= 5;
  b.rotate( i * 1.8 );
  //b.fillColor.hue = i * 4;

}
