paper.project.layers[0].removeChildren();

v.blocks = [];

for (var i = 64, l = 0; i > l; i--) {
   var c = new Path.Circle( [ 300, 300 ], Math.sqrt(i) * 35);
   v.blocks[i] = c;
//  v.blocks[i].strokeColor = 'white';
  c.fillColor = '#f0f';
  c.fillColor.hue = 150 - (i * 2.5);

  c.fillColor.brightness =  i / 12;

  //c.position.x += Math.random();
  //c.position.y += Math.random();

  if (i % 2) {
    c.fillColor = 'black';
  }
}
