window.dots = [];
v.dotcount = 4399;

paper.project.layers[0].removeChildren();

for (var i = 0, l = v.dotcount; i < l; i++) {
  dots[i] = new Path.Circle( [ 100, 100 ], 1 );  
}


var offset   = 310 * canvas.sizeRatio;

for (var i = 0, l = v.dotcount; i < l; i++) {
  if (i > 4000 || i < 10) continue;
  var x = canvas.sizeRatio * (Math.cos(i * twist) * (i * spread)),
      y = canvas.sizeRatio * (Math.sin(i * twist) * (i * spread)),
      size = (Math.sqrt(i) * embiggen * radius) * canvas.sizeRatio;

  if (linear_big) size = radius;
  window.c = dots[i];
  c.segments[2].point.x = 0;
  c.segments[2].point.y = 0;

  //var path = new Path.Rectangle(new Point(80, 25), size);
  c.position.x = offset + x;
  c.position.y = offset + y;
  //if (x < -300 || x > 300 || y < -300) continue;
  c.fillColor = '#333';
  if (i% 15) continue;
  c.fillColor.red   = (i %  3 == 0) ? 1 : 0.5;
  c.fillColor.green = (i %  4 == 0) ? 1 : 0.5;
  c.fillColor.blue  = (i %  8 == 0)  ? 1 : 0.5;
  c.fillColor.green += Math.sin(i/143);
  c.fillColor.blue  += Math.sin(i/210);
  c.fillColor.red   += Math.sin(i/280);
}
